async function getPointsOfInterest(a){const b=await fetch(a);return b.json()}async function initMap(){const a=await getPointsOfInterest("/poi");console.log(a);const b=new google.maps.Map(document.getElementById("map"),{zoom:16,center:{lat:52.568774,lng:-3.918031},mapTypeId:"hybrid",disableDefaultUI:!0,clickableIcons:!1});a.forEach(a=>{const c=new google.maps.Marker({position:{lat:a.latitude,lng:a.longitude},map:b,title:a.name,description:a.description});c.addListener("click",function(){const a=document.getElementById("poiCard");a.querySelector("#poi_title").innerHTML=c.title,a.querySelector("#poi_description").innerHTML=c.description;const b=M.Modal.init(a,{dismissible:!0,inDuration:500,outDuration:500});b.open()})})}